<template>
  <div class="container">
    <div class="row">
      <div class="col-4" v-for="column in columnList" :key="column.id">
        <div class="card h-100 shadow-sm">
          <img :src="column.avatar" :alt="column.title" class="card-img-top">
          <div class="card-body text-center">
            <h5 class="card-title">{{column.title}}</h5>
            <p class="card-text text-left">{{column.description}}</p>
            <router-link :to="`/detail/${column.id}`" class="btn btn-outline-primary">进入专栏</router-link>
          </div>
        </div>
      </div>
    </div>
  </div>
  
</template>

<script lang="ts">
import { computed, defineComponent, PropType } from 'vue';

import axios from 'axios';

export interface ColumnProps{
  id: number;
  title: string;
  avatar?: string;
  description: string;
}
export default defineComponent({
  name: 'ColumnList',
  props: {
    list: {
      type: Array as PropType<ColumnProps[]>,
      required: true
    }
  },
  setup(props){
    const columnList = computed(() => {
      return props.list.map(column => {
        if(!column.avatar){
          column.avatar = 'data:image/jpeg;base64,'
        }
        return column
      })
    })

    return {
      columnList
    }
  }
});
</script>

